CustomField.formula:
(
	IF(
		OR(
			AND(
				WEEKDAY(Contact__r.WelcomeCall_Issued_Date__c ) = 6,
				NOT(Contact__r.Is_Unit_Link_Product__c)
			),
			AND(
				OR(WEEKDAY(Contact__r.WelcomeCall_Issued_Date__c ) = 2, WEEKDAY(Contact__r.WelcomeCall_Issued_Date__c ) = 3),
				Contact__r.Is_Unit_Link_Product__c
			)
		),
		17,
		IF(
			AND(
				WEEKDAY(Contact__r.WelcomeCall_Issued_Date__c ) != 6,
				NOT(Contact__r.Is_Unit_Link_Product__c)
			),
			15,
			19
		)
	)
)
