WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/53.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/53.0/apex.js")}
{!REQUIRESCRIPT("/support/console/26.0/integration.js")}

var caseList = sforce.connection.query("SELECT Id,CallNote_RecordTypeId_Keeper__c, Account.Id, Entity__c FROM Contact WHERE id ='{!Call_Line__c.ContactId__c}'");
var records = caseList.getArray("records"); 
var Record_Type_Keeper__c,AccountId,entity;  
if (records.length > 0) { 
for (var i=0; i< records.length; i++) {
    Record_Type_Keeper__c = records[i].CallNote_RecordTypeId_Keeper__c;
    AccountId = records[i].Account.Id;
    entity = records[i].Entity__c ;
}
} 

var urlCallNote = ''; 

if (entity  == 'AMFS') {
urlCallNote = "/500/e?retURL=/{!Call_Line__c.Id}&def_contact_id={!Call_Line__c.ContactId__c}&def_account_id="+AccountId+"&RecordType="+Record_Type_Keeper__c+"&cas5=Inquiry&ent=Case";
} else {
urlCallNote = "/500/e?retURL=/{!Call_Line__c.ContactId__c}&def_contact_id={!Call_Line__c.ContactId__c}&def_account_id="+AccountId+"&RecordType="+Record_Type_Keeper__c+"&cas5=Inquiry&ent=Case&callLineId={!Call_Line__c.Id}";
}

if (sforce.console.isInConsole()) {
sforce.console.getEnclosingTabId(function(enclosingResult){
sforce.console.getEnclosingPrimaryTabId(function(){
sforce.console.openPrimaryTab(null, urlCallNote, true, 'Create Call Note', null);
});
});

}else {
window.location.href = urlCallNote;
}
