var caseList = sforce.connection.query("SELECT Id,Request_RecordTypeId_Keeper__c, Account.Id, Entity__c FROM Contact WHERE id ='{!Call_Line__c.ContactId__c}'");
var records = caseList.getArray("records"); 
var Record_Type_Keeper__c,AccountId,entity;  
if (records.length > 0) { 
for (var i=0; i< records.length; i++) {
    Record_Type_Keeper__c = records[i].Request_RecordTypeId_Keeper__c;
    AccountId = records[i].Account.Id;
    entity = records[i].Entity__c ;
}
} 

var urlCallNote = ''; 

if (entity  == 'AMFS') { 

urlRequest = "/apex/CaseRequestLayoutContact?retURL=/{!Call_Line__c.Id}&def_contact_id={!Call_Line__c.ContactId__c}&def_account_id="+AccountId+"&RecordType="+Record_Type_Keeper__c +"&00N9000000CD53R=0&cas5=Request&ent=Case&&cas11=none&entity={!Call_Line__c.Entity__c}";
} else {

urlRequest = "/apex/CaseRequestLayoutContact?retURL=/{!Call_Line__c.ContactId__c}&def_contact_id={!Call_Line__c.ContactId__c}&def_account_id="+AccountId+"&RecordType="+Record_Type_Keeper__c +"&00N9000000CD53R=0&cas5=Request&ent=Case&&cas11=none&entity={!Call_Line__c.Entity__c}";
}

if (sforce.console.isInConsole()) {
sforce.console.getEnclosingTabId(function(enclosingResult){
sforce.console.getEnclosingPrimaryTabId(function(){
sforce.console.openPrimaryTab(null, urlRequest, true, 'Create New Request', null);
});
});

}else {
window.location.href = urlRequest;
}
