var url = "/apex/ChangePaymentPage?contact_id={!Call_Line__c.ContactId__c }&entity={!Call_Line__c.Entity__c}&EnablePaymentType=TRUE";
console.log("url : " + url);

if (sforce.console.isInConsole()) {
var focusedPrimaryTabId, allPrimaryTabIds;
(function (c) {
"use strict";

c.getFocusedPrimaryTabId(function (result) {
focusedPrimaryTabId = result.id;
});
c.getPrimaryTabIds(function (result) {
allPrimaryTabIds = result.ids;
});
})(sforce.console);

sforce.console.openSubtab(focusedPrimaryTabId, url, true, 'PCIDSS', null,null, 'PCIDSS');
sforce.console.focusSubtabByNameAndPrimaryTabId('PCIDSS', focusedPrimaryTabId);


}else {
window.location.href = url;
}
