WebLink.url:
var entity = "{!TEXT($User.Entity__c)}"; 
{!REQUIRESCRIPT("/soap/ajax/29.0/connection.js")} 

var o = new sforce.SObject("Call_List__c"); 
o.id = "{!Call_List__c.Id}"; 
//o.IsExpired__c = "{!Call_List__c.is_Expired_F__c}"; 
var expF = "{!Call_List__c.is_Expired_F__c}"; 
o.IsExpired__c = "{!Call_List__c.IsExpired__c}"; 

//entity == "ALI"
if(entity == "ALI"){ 
if(o.IsExpired__c == "1" || expF == "1"){ 
o.Delete_Call_Line__c = "TRUE"; 

sforce.connection.update([o]); 
window.location.reload();

}else{ 
alert("Delete Call Line, only Expired.."+o.IsExpired__c); 
}
}else{ 
if(o.IsExpired__c == "0" || expF == "0"){ 
alert("Delete Call Line, Success.. "); 
o.Delete_Call_Line__c = "TRUE"; 

sforce.connection.update([o]); 
window.location.reload();

}else{ 
alert("Delete Call Line, only NOT Expired.."+o.IsExpired__c); 
}


//alert("Button Delete Call Line only for Entity ALI & AMFS. contact your administrator, if want it."); 
}
