sforce.connection.sessionId="{!$Api.Session_ID}";
var user = sforce.connection.getUserInfo();
//alert(user.userName);
//alert(user.roleId);
//alert(user.userId);
//alert(user.profileId);

var cs = sforce.connection.query("SELECT id, ownerid, named_user__c from case where id ='{!Case.Id}' limit 1");
var records = cs.getArray("records");
//alert(cs);
//alert(records );
var myObj = records[0];

myObj.OwnerId = user.userId;
myObj.Named_User__c = '{!User.Name}';

//alert(myObj);

try{
var result1 = sforce.connection.update([myObj]);
if(result1[0].getBoolean("success"))
{
alert('Success');
window.location.reload();
}
else{
alert('Error : '+result1);
}
}catch(err){
alert(err);
}
