{!REQUIRESCRIPT("/soap/ajax/10.0/apex.js")}
{!REQUIRESCRIPT("/support/console/26.0/integration.js")}

var urlCHS = "/apex/EmailMessageHistory?id={!Case.Id}";


if (sforce.console.isInConsole()) {
sforce.console.getEnclosingTabId(function(enclosingResult){
sforce.console.getEnclosingPrimaryTabId(function(){
sforce.console.openPrimaryTab(null, urlCHS, true, 'Email Conversation (PDF)', null);
});
});
}else {
window.open(urlCHS,'_blank');
}
