var urlInquiry = "/apex/TransferContactALI?retURL=/{!Contact.Id}&id={!Contact.Id}"; 

if (sforce.console.isInConsole()) { 
var focusedPrimaryTabId, allPrimaryTabIds; 
(function (c) { 
"use strict"; 

c.getFocusedPrimaryTabId(function (result) { 
focusedPrimaryTabId = result.id; 
}); 
c.getPrimaryTabIds(function (result) { 
allPrimaryTabIds = result.ids; 
}); 
})(sforce.console); 

sforce.console.openSubtab(focusedPrimaryTabId, urlInquiry, true, 'Create New Inquiry', null,null, 'assigntab'); 
sforce.console.focusSubtabByNameAndPrimaryTabId('assigntab', focusedPrimaryTabId); 


}else { 
window.location.href = urlInquiry; 
}
